import React, {Component} from "react";
import axios from "axios";
import logo from "./assets/logos/Logo-brainflix.svg";
import search from "./assets/icons/Icon-search.svg";
import upload from "./assets/icons/Icon-upload.svg";
import user from "./assets/images/Mohan-muruge.jpg";
import avatar from "./assets/images/User-image.png";
import views from "./assets/icons/Icon-views.svg";
import likes from "./assets/icons/Icon-likes.svg";
import Header from "./components/Header";
import VideoPlayer from "./components/VideoPlayer";
import CommentsList from "./components/CommentsList";
import VideoList from "./components/VideoList";
import Test from "./components/Test";

//import video data to use in state object
// import videoDetailsData from "./data/video-details.json";
// import videosData from "./data/videos.json";
import VideoDetails from "./components/VideoDetails";
import CommentForm from "./components/CommentForm";

const apiURL = "https://project-2-api.herokuapp.com";
const apiKey = Â "e2a28022-e948-4ff4-a990-33dd5ab61d89";

class App extends Component{
  //state object
  state = {
    nowPlayingID: "1af0jruup5gu",
    nowPlayingIndex: 0,
    videos: [],
    videoDetails:{
      comments:[]
    }

  }

  componentDidMount(){
    this.getVideosData();
    this.getVideosDetailsData();
  }

  // componentDidUpdate(prevProps, prevState) {
  //   console.log("comp updated", this.props);
  //   const { id } = this.props.match.params; 
  //   if (id !== undefined && prevState.videoDetails.id !== Number(id)) {
  //     this.getVideosDetailsData(id);
  //   }
  // }


  getVideosData = ()=>{
    axios
    // .get('https://project-2-api.herokuapp.com/videos?api_key=66840eb8-8aca-46f7-be41-b5b4b62bbc41')
      .get(`${apiURL}/videos?api_key=${apiKey}`)
      .then((response)=>{
        console.log(response);
        this.setState({
          videos: response.data,
        });
      })
      .catch((error)=>console.log(error));
  };

  getVideosDetailsData = (id)=>{
    axios
      .get(`https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=e2a28022-e948-4ff4-a990-33dd5ab61d89`)
      //  .get(`https://project-2-api.herokuapp.com/videos/${id}?api_key=e2a28022-e948-4ff4-a990-33dd5ab61d89`)
      // .get(`${apiURL}/videos/${id}?api_key=${apiKey}`)
      .then((response)=>{
        console.log("user object from api",response.data);
        this.setState({
          videoDetails: response.data,
        });
      })
      .catch((error)=>console.log("couldnt load on time",error));
  };

  setNowPlayingVideo = (id) => {
    const foundIndex = this.state.videos.findIndex((video) => video.id === id);
    // set current video to the id from videoList click event and also set the index of the current video
    this.setState({
      nowPlayingID: id,
      nowPlayingIndex: foundIndex,
    });
  };

  render() {
    const {videos, videoDetails, nowPlayingID} = this.state;
    return (
      <div className="App">
        <Header logo={logo} search={search} upload={upload} user={user}/>
        {/* <VideoPlayer posterImage={this.state.videoDetails[this.state.nowPlayingIndex]} />  */}
        <VideoPlayer posterImage={videoDetails} />      
        {/* <VideoPlayer posterImage={this.state.videoDetails[0].image} />      */}

        {/* <Test 
        // userData={this.state.videoDetails} 
        // currentVideoID={this.state.nowPlayingID}
        // setNowPlayingVideo={this.setNowPlayingVideo}
        views={views} 
        likes={likes} 
        videoDetailsData={this.state.videoDetails}
        // CommentListDetails={this.state.videoDetails}
        /> */}


        <main className="main-section">
          <div className="main-section__left">
            <VideoDetails 
            views={views} 
            likes={likes} 
            videoDetailsData={videoDetails}/>

            <CommentForm user={user} videoDetailsData={videoDetails}/>

            <CommentsList
            avatar={avatar}
            videoDetailsData={this.state.videoDetails}/>
          </div>

          <aside className="main-section__right">
            <VideoList
            videos={videos}
            currentVideoID={nowPlayingID}
            setNowPlayingVideo={this.setNowPlayingVideo}/>
          </aside>           
        </main>        
      </div>
    );
  }
}

export default App;
