import React from 'react'
import TimeStamp from './TimeStamp';

function CommentsList(props) {
    const { id } = props;
    console.log(props);
    return (
        <div>
            <ul id="commentlist" className= "commentlist"> 
                {props.videoData.comments.map ((comment) => (
                    // console.log(comment);
                    <div className="commentlist__container">
                        <li key={id} className="commentlist__item">
                            <img className="commentlist__userimage" src={props.avatar} alt="user image"/>
                            <div className="commentlist__group">
                                <div className="commentlist__commentgroup">
                                    <p className="commentlist__username">{comment.name}</p>
                                    <TimeStamp className="commentlist__timestamp" timestamp={comment.timestamp}/>
                                    {/* <p className="commentlist__timestamp">{new Date(comment.timestamp).toLocaleDateString()}</p> */}
                                </div>
                                <p className="commentlist__usercomment">{comment.comment}</p>
                            </div>
                        </li>
                        <hr className="commentlist__divider"/>
                    </div>                        
                ))}                         
            </ul> 
        </div>
    )
}

export default CommentsList
