import React from "react";
import logo from "./assets/logos/Logo-brainflix.svg";
import search from "./assets/icons/Icon-search.svg";
import upload from "./assets/icons/Icon-upload.svg";
import user from "./assets/images/Mohan-muruge.jpg";
import views from "./assets/icons/Icon-views.svg";
import likes from "./assets/icons/Icon-likes.svg";
import Header from "./components/Header";
import VideoPlayer from "./components/VideoPlayer";
// import './App.css';

//import video data to use in state object
import videoDetailsData from "./data/video-details.json";
import videosData from "./data/videos.json";

class App extends React.Component{
  //state object
  state = {
    videos: videosData,
    videoDetails: videoDetailsData
  }



  render() {
    return (
      <div className="App">
        <Header logo={logo} search={search} upload={upload} user={user}/>
        <VideoPlayer posterImage={this.state.videoDetails[0].image} />

        <article className="main-video">
          <section className="main-video__info">
            <h1 className="main-video__title">{this.state.videoDetails[0].title}</h1>
            <div>
              <h2 className="main-video__name">By {this.state.videoDetails[0].channel}</h2>
              <p className="main-video__date">{new Date(this.state.videoDetails[0].timestamp).toLocaleDateString()}</p>
            </div>
            {/* <div className="main-video__views">
              <img className="main-video__viewsicon" src={views} alt="views icon" />

            </div> */}

          </section>

        </article>










      </div>
    );
  }


}

export default App;
