import React from "react";
import logo from "./assets/logos/Logo-brainflix.svg";
import search from "./assets/icons/Icon-search.svg";
import upload from "./assets/icons/Icon-upload.svg";
import user from "./assets/images/Mohan-muruge.jpg";
import avatar from "./assets/images/User-image.png";
import views from "./assets/icons/Icon-views.svg";
import likes from "./assets/icons/Icon-likes.svg";
import Header from "./components/Header";
import VideoPlayer from "./components/VideoPlayer";
import CommentsList from "./components/CommentsList";
import VideoList from "./components/VideoList";
// import './App.css';

//import video data to use in state object
import videoDetailsData from "./data/video-details.json";
import videosData from "./data/videos.json";
import VideoDetails from "./components/VideoDetails";
import CommentForm from "./components/CommentForm";


// const comments=[
//   {
//       "name": "Micheal Lyons",
//       "comment": "They BLEW the ROOF off at their last show, once everyone started figuring out they were going. This is still simply the greatest opening of acconcert I have EVER witnessed.",
//       "id": "1ab6d9f6-da38-456e-9b09-ab0acd9ce818",
//       "likes": 0,
//       "timestamp": 1545162149000
//   },
//   {
//       "name": "Gary Wong",
//       "comment": "Every time I see him shred I feel so motivated to get off my couch and hop on my board. He’s so talented! I wish I can ride like him one day so I can really enjoy myself!",
//       "id": "cc6f173d-9e9d-4501-918d-bc11f15a8e14",
//       "likes": 0,
//       "timestamp": 1544595784046
//   },
// ];

class App extends React.Component{
  //state object
  state = {
    videos: videosData,
    videoDetails: videoDetailsData
  }

  render() {
    return (
      <div className="App">
        <Header logo={logo} search={search} upload={upload} user={user}/>
        <VideoPlayer posterImage={this.state.videoDetails[0].image} />

        <article className="main-video">
          <VideoDetails views={views} likes={likes} videoDetailsData={this.state.videoDetails[0]}/>

          {/* Join the Conversation */}
          <CommentForm user={user} CommentListDetails={this.state.videoDetails[0]}/>

        <CommentsList
          // videos={this.state.videos}
          avatar={avatar}
          videoData={this.state.videoDetails[0]}
        />

        {/* <VideoList
          videos={this.state.videos}
          currentVideo={this.state.currentVideo}
        /> */}

        </article>

        <section className="videolist">
          <div className="videolist__container">
            <p className="videolist__heading">NEXT VIDEO</p>
            <li className="videolist__item">
                <img className="videolist__image" src={this.state.videos[0].image} alt="video poster image"/>
                  <div className="videolist__group">
                    <p className="videolist__title">{this.state.videos[0].title}</p>
                    <p className="videolist__channelname">{this.state.videos[0].channel}</p>    
                  </div>             
            </li>
          </div>        
        </section>

      </div>
    );
  }

}

export default App;
